import React, { useEffect, useState } from "react";
import axios from "axios"; 


const Contact = (req, res) => {
    const [name, setName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [message, setMessage] = useState("");
    const [sent, setSent] = useState(false);

    const handleName = (e) => {
        setName(e.target.value);
    }

    const handleLastName = (e) => {
        setLastName(e.target.value);
    }
    const handleEmail = (e) => {
        setEmail(e.target.value);
    }
    const handleMessage = (e) => {
        setMessage(e.target.value);
    }
    const formSubmit = (e) => {
        e.preventDefault();
        let data = {
            name : name,
            lastName: lastName,
            email: email,
            message: message

        } 
        axios.post('/api/forma', data)
        .then(response => {
            setSent(true)

        }).catch(() => {
            console.log("message not sent")
        })

    }

    const resetForm = () => {
        // setName(""),
        setLastName(""),
        setEmail(""),
        setMessage("")
        setTimeout(()=> {
            setSent(false)
        }, 3000)
    }

    return (
        <div className="contener">
            <form className="formContact">
                <div>
                    <label htmlFor="name">Nom</label>
                    <input value={name} className="name" type="text" name="name" placeholder="Votre nom" onChange={handleName} />
                </div>
                <div>
                    <label htmlFor="prenom">Prénom</label>
                    <input value={lastName} className="prenom" type="text" name="prenom" placeholder="Votre prenom" onChange={handleLastName} />
                </div>
                <div>
                    <label htmlFor="email">E-mail</label>
                    <input value={email} className="email" type="email" name="email" placeholder="Vote E-mail" onChange={handleEmail} />
                </div>

                <div>
                    <label htmlFor="message">Message</label>
                    <textarea value={message} cols="30" rows="5" className="message" type="text" name="message" placeholder="Vote message" onChange={handleMessage} />
                </div>
                <div>{name}</div>
                <div>
                    <button type="submit">Envoyer</button>
                </div>
            </form>

        </div>
    )
}


export default Contact;
