import React, { useState } from "react";
import axios from "axios";


const Contact = () => {
    const [name, setName] = useState("");
    const [lastName, setLastName] = useState("");
    const [email, setEmail] = useState("");
    const [message, setMessage] = useState("");
    const [sent, setSent] = useState(false);

    const handleName = (e) => {
        setName(e.target.value);
    }

    const handleLastName = (e) => {
        setLastName(e.target.value);
    }
    const handleEmail = (e) => {
        setEmail(e.target.value);
    }
    const handleMessage = (e) => {
        setMessage(e.target.value);
    }
    const formSubmit = (e) => {
        e.preventDefault();
        let data = {
            name: name,
            lastName: lastName,
            email: email,
            message: message

        }
        axios.post('/contact', data)
            .then(response => {
                setSent(true);
                resetForm();

            }).catch(() => {
                console.log("message not sent")
            })

    }

    const resetForm = () => {
        setName("");
        setLastName("");
        setEmail("");
        setMessage("");
        setTimeout(() => {
            setSent(false)
        }, 3000)
    }

    return (
        <div className="contener">
            <h3>FORMULAIRE DE CONTACT</h3>
            <form onSubmit={formSubmit} className="formContact">
                <div>
                    <label htmlFor="name">Nom</label>
                    <input style={{marginLeft: "20px"}} value={name} className="name" type="text" name="name" placeholder="Votre nom" onChange={handleName} />
                </div>
                <div>
                    <label htmlFor="prenom">Pr√©nom</label>
                    <input style={{marginLeft: "20px"}} value={lastName} className="prenom" type="text" name="prenom" placeholder="Votre prenom" onChange={handleLastName} />
                </div>
                <div>
                    <label htmlFor="email">E-mail</label>
                    <input style={{marginLeft: "20px"}} value={email} className="email" type="email" name="email" placeholder="Vote E-mail" onChange={handleEmail} />
                </div>
                <div>
                    <label htmlFor="message">Message</label>
                    <textarea value={message} cols="40" rows="6" className="message" type="text" name="message" placeholder="Vote message" onChange={handleMessage} />
                </div>
                <div className={sent ? "msg msgAppear" : "msg"}>Message has been sent</div>
                <div className="btn">
                    <button type="submit">Envoyer</button>
                </div>
            </form>

        </div>
    )
}


export default Contact;
